mapper.select:
SELECT t1.id, t1.title as content, t1.gmt_create as gmtCreate, t1.presentor, t1.presentor_name as nickname FROM ask_special t1 LEFT JOIN user_account t2 on t1.presentor = t2.id where 1=1 and t1.category = 1
and CONCAT(t1.title,t1.presentor_name) like CONCAT('%',#{name},'%' )
and t1.begin_time = #{startTime}
and t1.end_time = #{endTime}
and t1.begin_time >= #{startTime} and t1.end_time <= #{endTime}
order by t1.gmt_create desc

SELECT t1.id, t1.title as content, t1.gmt_create as gmtCreate, t1.presentor, t1.presentor_name as nickname FROM ask_special t1 LEFT JOIN user_account t2 on t1.presentor = t2.id where 1=1 and t1.category = 2
and CONCAT(t1.title,t2.nickname) like CONCAT('%',#{name},'%' )
and t1.begin_time = #{startTime}
and t1.end_time = #{endTime}
and t1.begin_time >= #{startTime} and t1.end_time <= #{endTime}
order by t1.gmt_create desc

select.if:
SELECT t2.id, t2.`name` as content, t3.nickname, t2.gmt_create as gmtCreate, t2.presentor, t1.category, t1.content as con FROM user_report t1 left JOIN ask_info t2 on t1.biz_id = t2.id left JOIN user_account t3 on t3.id = t2.presentor where 1=1
and CONCAT(t2.name,t3.nickname) like CONCAT('%',#{name},'%' )
and t1.gmt_create = #{startTime}
and t1.gmt_create = #{endTime}
and t1.gmt_create >= #{startTime} and t1.gmt_create <= #{endTime}
and t2.`status` != 9 and t1.category = 1 order by t1.gmt_create desc

SELECT t2.id, t2.`name` AS content, t3.nickname, t2.gmt_create AS gmtCreate, t2.creator as presentor, t1.category, t1.content as con FROM user_report t1 LEFT JOIN club_info t2 ON t1.biz_id = t2.id LEFT JOIN user_account t3 ON t3.id = t2.creator WHERE 1 = 1
and CONCAT(t2.name,t3.nickname) like CONCAT('%',#{name},'%' )
and t1.gmt_create = #{startTime}
and t1.gmt_create = #{endTime}
and t1.gmt_create >= #{startTime} and t1.gmt_create <= #{endTime}
and t2.`status` != 9 and t1.category = 2 order by t1.gmt_create desc
