mapper.select:
select t1.id,t1.club_id as clubId,t1.presentor,t1.gmt_create as gmtCreate,t1.title,t1.content, t1.apply_deadline as applyDeadline,t1.limit_member as limitMember,address_name as addressName, t2.nickname as presentorName,t2.header from club_activity t1 left join user_account t2 on t1.presentor = t2.id where t1.id in ( select max(id) from club_activity where club_id in
#{clubId}
and status = 0 group by club_id )

SELECT t1.id, t1.title, t1.content, t1.presentor, t1.gmt_create as gmtCreate, t2.nickname FROM club_activity t1 LEFT JOIN user_account t2 on t1.presentor = t2.id where 1=1 and t1.status = 0 and t1.club_id = #{clubId}
and CONCAT(t1.title,t2.nickname) like CONCAT('%',#{name},'%' )
and t1.gmt_create = #{startTime}
and t1.gmt_create = #{endTime}
and t1.gmt_create >= #{startTime} and t1.gmt_create <= #{endTime}
ORDER BY t1.gmt_create desc
