mapper.insert:

insert into club_speak (id, club_id, presentor, 
  gmt_create, content, itsaid_flag, 
  like_amount, comment_amount, status
  )
values (#{id,jdbcType=BIGINT}, #{clubId,jdbcType=INTEGER}, #{presentor,jdbcType=INTEGER}, 
  #{gmtCreate,jdbcType=TIMESTAMP}, #{content,jdbcType=VARCHAR}, #{itsaidFlag,jdbcType=TINYINT}, 
  #{likeAmount,jdbcType=INTEGER}, #{commentAmount,jdbcType=INTEGER}, #{status,jdbcType=TINYINT}
  )

mapper.select:
select t1.id,t1.club_id as clubId,t1.presentor,t1.gmt_create as gmtCreate,t1.content, t1.like_amount as likeAmount,t1.comment_amount AS commentAmount,t1.itsaid_flag as itsaidFlag, t2.nickname as presentorName,t2.header from club_speak t1 left join user_account t2 on t1.presentor = t2.id where t1.status = 0
and t1.club_id = #{clubId}
and t1.itsaid_flag = 1
and t1.presentor = #{uid}
order by t1.gmt_create desc

select t1.id,t1.club_id as clubId,t1.presentor,t1.gmt_create as gmtCreate, t1.content,t1.like_amount as likeAmount,t1.comment_amount AS commentAmount, t1.itsaid_flag as itsaidFlag, t2.nickname as presentorName,t2.header from club_speak t1 left join user_account t2 on t1.presentor = t2.id where t1.id in ( select max(id) from club_speak where club_id in
#{clubId}
and status = 0 group by club_id )

SELECT t1.id, t1.content, t1.presentor, t1.gmt_create as gmtCreate, t2.nickname FROM club_speak t1 LEFT JOIN user_account t2 on t1.presentor = t2.id where 1=1 and t1.status = 0 and t1.club_id = #{clubId}
and CONCAT(t1.content,t2.nickname) like CONCAT('%',#{name},'%' )
and t1.gmt_create = #{startTime}
and t1.gmt_create = #{endTime}
and t1.gmt_create >= #{startTime} and t1.gmt_create <= #{endTime}
order by t1.gmt_create desc
