mapper.select:
      select t1.id,t1.club_id as clubId,t1.presentor,t1.gmt_create as gmtCreate,
      t1.title,t1.content,t1.like_amount as likeAmount,
      t1.comment_amount AS commentAmount,t1.recommend,
      t2.nickname as presentorName,t2.header
      from club_team_building t1 left join user_account t2 on t1.presentor = t2.id
      where 1=1
      and t1.status = 0
      and t1.club_id = #{clubId}

         and CONCAT(t1.title, t2.nickname) like CONCAT('%',#{title},'%')


		 and t1.gmt_create = #{startTime}


		 and t1.gmt_create = #{endTime}


		 and t1.gmt_create >= #{startTime} 
		 and t1.gmt_create <= #{endTime} 

      order by t1.gmt_create desc
