mapper.insert:

insert into club_vote_info (id, gmt_create, gmt_modified, 
  club_id, presentor, name, 
  introduction, deadline, status
  )
values (#{id,jdbcType=BIGINT}, #{gmtCreate,jdbcType=TIMESTAMP}, #{gmtModified,jdbcType=TIMESTAMP}, 
  #{clubId,jdbcType=INTEGER}, #{presentor,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, 
  #{introduction,jdbcType=VARCHAR}, #{deadline,jdbcType=TIMESTAMP}, #{status,jdbcType=TINYINT}
  )

mapper.select:
SELECT t1.id, t1.name as content, t1.gmt_create as gmtCreate, t1.presentor, t2.nickname FROM club_vote_info t1 LEFT JOIN user_account t2 on t1.presentor = t2.id where t1.club_id = #{clubId} and t1.status != 9
and CONCAT(t1.name,t2.nickname) like CONCAT('%',#{name},'%' )
and t1.gmt_create = #{startTime}
and t1.gmt_create = #{endTime}
and t1.gmt_create >= #{startTime} and t1.gmt_create <= #{endTime}
order by t1.gmt_create desc
