mapper.select:
	select * from user_account where role_id is null

	 and nickname LIKE concat( '%',#{name},'%' )


	 and telephone LIKE concat( '%',#{phone},'%' )


	 and college LIKE concat( '%',#{college},'%' )


	 and cert_flag = #{status}


	 and enable = #{enable}


	 and gmt_create >= #{startTime}


	 and gmt_create  <= #{endTime}


	 and gmt_create >= #{startTime}
	 and gmt_create <= #{endTime}

ORDER BY gmt_create desc
